import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import ChatInput from "./ChatInput";

import Message from "./Message";
import db from "./firebase";

import "./Chat.css";
import StarBorderOutlinedIcon from "@material-ui/icons/StarBorderOutlined";
import InfoOutlinedIcon from "@material-ui/icons/InfoOutlined";

function Chat() {
	const { barId, tableId } = useParams();
	const [tableDetails, setTableDetails] = useState(null);
	const [tableMessages, setTableMessages] = useState([]);

	console.log("In chat, barId is ", barId)
	console.log("In chat, tableId is ", tableId)

	let data;

	useEffect(() => {

		if (tableId) {
			data = db.collection("bars")
				.doc(barId)
				.collection("tables")
				.doc(tableId)
				.onSnapshot((snapshot) => {
					setTableDetails(snapshot.data())

				}
				);

		}


		console.log("Table details: ", data)

		// db.collection("tables")
		// 	.doc(tableId)
		// 	.collection("messages")
		// 	// .orderBy("timestamp", "asc")
		// 	.onSnapshot((snapshot) =>
		// 		setTableMessages(snapshot.docs.map((doc) => doc.data()))
		// 	);

		console.log("Table messages: ", tableMessages)

	}, [tableId]);





	return (
		<div className="chat">
			<div className="chat__header">
				<div className="chat__headerLeft">
					<h4 className="chat__channelName">
						<strong>#{tableDetails?.name}</strong>
						<StarBorderOutlinedIcon />
					</h4>
				</div>
				<div className="chat__headerRight">
					<p>
						<InfoOutlinedIcon /> Details
					</p>
				</div>
			</div>
			<div className="chat__messages">
				{tableMessages.map(({ message, user }) => (
					<Message
						message={message}
						user={user}
					/>
				))}
				this is where the chats go
			</div>
			<ChatInput channelName={tableDetails?.name} channelId={tableId} />
		</div>
	);
}

export default Chat;
