import React from "react";
import { useHistory } from "react-router-dom";
import db from "./firebase";
import "./BarListing.css";

function BarListing({ Icon, title, id, addChannelOption }) {

	const history = useHistory();

	const selectChannel = () => {
		if (id) {
			history.push(`/bar/${id}`);
		} else {
			history.push(title);
		}
	};

	// const addChannel = () => {
	// 	const channelName = prompt("Please enter a channel name");

	// 	if (channelName) {
	// 		db.collection("bars").add({
	// 			name: channelName,
	// 		});
	// 	}
	// };

	return (
		<div className="bar_listing" onClick={selectChannel}>
			{Icon && <Icon className="BarListing__icon" />}
			{Icon ? (
				<h3>{title}</h3>
			) : (
				<h3 className="BarListing__channel">
						<img
					src="https://cdn4.vectorstock.com/i/1000x1000/52/38/speed-beer-logo-icon-design-vector-22545238.jpg"
					alt="beer logo"
				/>
					<span className="BarListing__hash">#</span>
					{title}
				</h3>
			)}
		</div>
	);
}

export default BarListing;
